type ParagraphBlock = {
    type: "paragraph";
    data: {
        text: string;
    };
};
type QuoteBlock = {
    type: "quote";
    data: {
        text: string;
        caption?: string;
        alignment: "left" | "center";
    };
};
type SimpleImageBlock = {
    type: "image";
    data: {
        url: string;
        caption: string;
        withBorder?: boolean;
        withBackground?: boolean;
        stretched?: boolean;
    };
};
type HeaderBlock = {
    type: "header";
    data: {
        text: string;
        level: number;
    };
};
type ListBlock = {
    type: "list";
    data: {
        style: "unordered" | "ordered";
        items: string[];
    };
};
type RawBlock = {
    type: "raw";
    data: {
        html: string;
    };
};
type BlockData = ParagraphBlock | QuoteBlock | SimpleImageBlock | HeaderBlock | ListBlock | RawBlock;
type EditorContent = BlockData[];

declare const convertHtmlToBlocks: (html: Element) => EditorContent;

export { convertHtmlToBlocks };
